    width: 80px!important;
    margin-left: -80px!important;
    padding-left: 5px!important;
}


.hljs {
    background:#ffffff00;
}




.recommend-box, .article-list {
	margin-top: 30px;
}
.comment-list{
	padding:15px;
}

.aside-title{
height:60px}
.comment-list-box{
	display:block!important;
}
.p4course_target{
	display:none!important
}
}

@-moz-document regexp("https*(://)*.+(.csdn.net/).*") {
.indexSuperise,.slide-outer,.magazine_box,.fouce_img_box,.J_fouce_info,.markes,.fourth_column,#adContent


{
    display: none!important;
    width: 0px!important;
