style.css:
@-moz-document url-prefix(https://www.google.) {
  
/*Gmail Images Notifications & all apps opacity*/
.gb_Ag.gb_Ua, .gb_Ra {opacity: 0.3; transition: .3s ease-out !important;}
.gb_Ag.gb_Ua:hover, .gb_Ra:hover {opacity: 1; transition: .6s ease-out !important;}

/*Hide Crap*/
.b0KoTc, #SIvCob, .Q8LRLc, .FPdoLc, #fsl, #fsr > a {display: none !important;}

/*Search Bar simplify*/
body, #appbar, .big .mw, #cnt, .srp #gs_st0, #searchform.big .RNNXgb, .RNNXgb, #hdtbSum, .sfbg {background: #fafafa !important;}

a.aciXEb {background: transparent !important;}

/*Search*/
.zthhpf, .RNNXgb, .sbibod {box-shadow: 0 0px 0px 0 rgba(0,0,0,0.16),0 1px 0 0 rgba(0,0,0,0.09) !important; border-radius: 0 !important; border: 1px !important;}
.qcTKEe ,.zthhpf, .jZWadf, .aajZCb, .minidiv .sfbg, .sbib_a, .sbico-c {background: #fafafa !important;}
.JUypV {display: none !important;}
.sbico-c {top: 1px !important;}

/*Simpler Google Search, I'm Feeling Lucky & Tools button*/
#hdtb-msb .hdtb-tl-sel, #hdtb-msb .hdtb-tl-sel:hover {background: #e0e0e0 !important; box-shadow: none !important;}
#hdtb-tls:hover {box-shadow: none; background-image: none;}
.jhp input[type="submit"]:hover, .sbdd_a input:hover, .gbqfba:hover {background-image: none !important; box-shadow: none !important;}
.jhp input[type="submit"], .sbdd_a input, .gbqfba {background-image: none !important;}

/*Footer*/
#fbar {opacity: 0.3; transition: .3s ease-out !important; background: #fafafa !important; border-top: 0 !important; padding-bottom: 10px !important; padding-right: 10px !important;}
#fbar:hover {opacity: 1; transition: .6s ease-out !important;}
.b2hzT {border-bottom: 0 !important;}
}
