style.css:
@-moz-document domain("zstio.bytom.pl"), domain("zst.bytom.pl") {
    .timetable .head {
    	color: #757575;
    }
    .timetable .cell .period-text, .timetable .cell .hour-number {
    	color: #757575;
	}
    .lesson {
    	color: #535353;
    }
    
    /*parzysty head-only*/
    .timetable .head .cell:nth-child(even){
    	background: #e0f6bc;
        color: #5b5b5b;
    }
    
    /*parzysty parzysty*/
	.timetable .cell:nth-child(even) {
    	background: #f8feeb;
	}
    
     /*nieparzysty parzysty*/
    .timetable .row:nth-child(even) {
        background: #ffffff;
    }
    
     /*parzysty nieparzysty*/
    .timetable .row:nth-child(even) .cell:nth-child(even){
        background: #eefada;
    }
    
     /*nieparzysty nieparzysty*/
    .timetable .row:nth-child(even) .cell:nth-child(odd){
        background: #fafafa;
    }
}
