style.css:
@-moz-document domain('web.threema.ch'){
div.conversation, #navigation-header, #main, #conversation #conversation-footer, #messenger #detail>div.ng-scope .detail-header, md-dialog, md-menu-content.md-default-theme, md-menu-content, md-dialog.md-default-theme, md-dialog, #messenger #detail>div.ng-scope, md-card._md{
        background-color: #212121;
    }
    .md-button.md-raised:not([disabled]){
        background-color: #1a1a1a;
    }
    #navigation-conversations .conversation-wrapper .conversation.active, #navigation-conversations .conversation-wrapper .conversation:hover , #navigation-contacts .contact:hover{ 
        background-color: #121212;
        border-left: 8px solid #050505;
    }

    span.title.ng-binding, #main{
        color: #eee;
    }

    span.ng-scope, span.ng-binding.ng-isolate-scope, span.no-draft, #navigation-conversations .conversation-wrapper .conversation .conversation-box .message-box .latest-message .message-text, md-dialog.md-default-theme, .md-button.md-default-theme.md-primary, .ng-binding{
        color: #ccc;
    }

    #navigation-conversations .conversation-wrapper .conversation, #navigation-contacts .contact{
        border-left: 8px solid #151515;
    }

    #navigation-conversations .conversation-wrapper{
        border-bottom: 1px solid #151515;
    }

    #background-image{
        filter: brightness(30%)
    }

    #title #logo svg{
        fill: #bbb;
    }
    .material-icons.md-medium-dark{
        color: #aaa;
    }

    #conversation{
        background-image: url(https://web.threema.ch/img/wallpaper_light.png);
        background-blend-mode: overlay;
        background-color: #222;
    }

    #conversation-chat .chat .message-out .message-body, #conversation-chat .chat .message-out .bubble-triangle::after{
        background-color: #2c3429;
    }
    #conversation-chat .chat .message-in .message-body, #conversation-chat .chat .message-in .bubble-triangle:after{
        background-color: #111;
    }

    compose-area>div:first-child>div:nth-of-type(2) div.compose, #navigation-header .search input[type="text"]{
        background-color: #333;
    }
canvas.qrcode{
    border: 5px solid white;
}
}
