style.css:
@-moz-document url-prefix("https://www.quora.com/") {
.QuestionArea.u-padding-bottom--xs .QuestionDetailsLegacy {display:none;}
.TopicList.QuestionTopicHorizontalList {opacity:.6;}
.QuestionArea .icon_action_bar {opacity:1;}


/* ads */
.jaalefvirubidoqmtohv {display:none}

/*======= Answer Count ======== */
.QuestionPageAnswerHeader > .answer_count {
    background-color:#f7f7f8; 
   font-weight:bold; 
   border-radius:50px;
    padding:5px 10px;
    max-width:130px;
}

/*======= Answer Cards ======== */
.pagedlist_item .Answer.AnswerBase {
        border: 1px solid #e2e2e2;
        background-color:#fafafa;
        border-radius: 5px;
        padding: 10px 20px;
        margin-bottom: 30px;
    }

.UnifiedAnswerPagedList {display:none;}
.UnifiedAnswerPagedList.PagedListFoo.unified {display:unset;}
.pagedlist_item > div {
        display:none;
    }
.pagedlist_item > div[data-clog-trigger="impression"] {
        display:unset;
    }

/*======= Answers ======== */
.u-serif-font-main--large {font-size:18px !important; margin-top:20px;}



/*======= Button ======== */
.AskQuestionButton.LookupBarAskQuestionModalButton {border: 1px solid #6081ff !important; background: #4f95ff !important;}

.action_bar_inner.u-flex > .ItemComponent.WriteAnswerPrimaryActionItem.primary_item .ui_button_label {color:#4f95ff;}

.ui_button--FlatStyle--blue .ui_button_icon .icon_svg-stroke, .ui_button--StackedStyle--blue .ui_button_icon .icon_svg-stroke {stroke:#4f95ff !important;}
}
