style.css:
@-moz-document url-prefix("https://to-do.microsoft") {
#main, .tasksToolbar, .main-background, .leftColumn, .leftColumn-exited, .chunkedComponentList, .details, .sticky, .baseAdd, .addTask, .backgroundLines {
    background : #212121!important;
}
.details-body, .taskItem.selected {
    background: #333333!important;
}
.taskItem.active {
    background-color: #9c9c9c!important;
}
.tasksToolbar, .listTitle, .baseAdd, .baseAdd-input, .taskItem-titleWrapper {
    color: #717070!important;
}

.placeholder, .empty-flagged  {
    background-color:  #222222!important;
}

.taskItem.selected .taskItem-body{
 background: black;   
}

.taskItem:hover, .taskItem:active {
 background: black;   
}
}
