/* ==UserStyle==
@name           jporta.iit.bme.hu - 2/18/2019, 9:00:18 PM
@namespace      github.com/openstyles/stylus
@version        1.2.0
@description    Jporta dark theme
@author         AndrisBorbas
==/UserStyle== */
@-moz-document domain("jporta.iit.bme.hu") {
    body {
        background-color: #222;
        color: #EEE;
    }

    .content {
        background-color: #222;
        color: #EEE;
    }

    a {
        color: #338bd6;
    }
    .help-block {
        color: #888;
    }
    small {
        color: #999;
    }
    strong {
        color: #c3e0ff;
    }
    pre {
        color: #DDD;
        background: #333;
    }
    img {
        background: #ddd;
        border: 2px solid #000;
        border-radius: 4px;
        box-shadow: 0px 0px 7px #fff;
    }
}
