style.css:
@-moz-document domain("astats.astats.nl") {
table#myTable {
    width: 100% !important;
    max-width: 80% !important;
    margin-bottom: 1rem;
    background-color: transparent;
    border-collapse: collapse;
}

table#myTable td, table#myTable th{
    border-width: 0 1px;
    border-color: rgba(255, 255, 255, 0.05);
    border-style: solid;
}

table#myTable tbody tr:hover {
    background-color: rgba(255,255,255,.075) !important;
}

td {
    padding: .75rem;
    text-align: inherit;
}
th {
    padding: .75rem;
    text-align: center;
    font-weight: bold;
}

/* Center numeric values */
td[align="right"] {
    text-align: center;
}

td.AchievementBox {
    text-align: center;
}

/* Images are being fetched from steam cdn and their native resolution is 64x64. */
td.AchievementBox img {
    width: 64px;
    height: 64px;
}
}
