style.css:
@-moz-document domain("drawabox.com") {
body {
    background-color: #333;
}

.wrapper {
    background-color: #222;
}

h1, h2, h3, h4, h5, h6, p, footer, li, pre, .notice {
    color: #fff;
}

aside.sidebar-lesson {
    background-color: #111;
}

aside.sidebar-lesson>ul>li.active,aside.sidebar-lesson>ul>li:hover {
    background-color: #000;
}

li.active,aside.sidebar-lesson>ul>li>ul>li:hover {
    background-color: #444;
}
}
