style.css:
@-moz-document url-prefix("https://gall.dcinside.com/mgallery/board"), url-prefix("https://gall.dcinside.com/board") {
/*더러운 것들 삭제*/
.right_content,
.issue_wrap,
.gnb_bar,
.dc_logo,
.stickyunit,
footer {
    display: none;
}

/* 반응형 웹*/
.list_wrap {
    min-width: 0;
}

.wrap_inner {
    width: auto;
}

#container {
    width: auto !important;
    margin: 0 20vw !important;
}

.left_content {
    width: 100% !important;
    margin-left: auto;
    margin-right: auto;
}

.dchead {
    width: auto !important;
    margin: 0 20vw !important;
}

.wrap_search {
    top: 39px;
    left: 50% !important;
    transform: translateX(-50%);
}

.list_array_option {
    width: 100%;
}

.cmt_txt_cont {
    width: 100% !important;
}

textarea {
    width: calc(100% - 26px) !important;
}

.ub-content:not(li) {
    height: 40px;
}

/*게시글 목록 설정*/
table.gall_list tr.ub-content:not(.us-post) {
    display: none;
}

.gall_tit {
    font-size: 16px;
}

.us-post {
    height: 40px;
}

.gall_list .gall_num {
    font-size: 0px
}
}
