style.css:
@-moz-document domain("www.random.org") {
body, #navigation {
    background: black;
    color: white;
}

#navigation a:link, 
#navigation a:visited, 
#invisible h2 {
    background: black;
}

p {
    background-color: black!important;
}

h1 {
    background-image: url(https://i.imgur.com/92pudwg.png);
}

div#search {
    color: white;
    background-color: black;
}

#navigation ul {
    background: black;
    box-shadow: none;
}

#navigation a:hover, 
a:hover {
    color: white;
}

#navigation a:link.active, 
#navigation a:visited.active, 
#navigation a:hover {
    color: white;
    background-color: black;
}

div.maingraphic1, 
div.maingraphic2 {
    color: black;
}

div.maingraphic1 {
    background-color: rgba(204, 255, 204, 0.5);
}

div.maingraphic2 {
    background-color: rgba(204, 204, 255, 0.5);
}

#invisible h3 {
    background-color: black;
}
}
