style.css:
@-moz-document regexp("^.*:\\/\\/.*quora\\.com(?!\\/profile\\/).*$") {
.grid_page {
    margin: 0 auto !important;
    width: 100% !important;
}


.layout_3col_left,
.layout_3col_right,
.layout_2col_side {
    display: none;
}


.layout_3col_center,
.layout_2col_main {
    padding: 0 !important;
    margin: 0 auto !important;
    width: 100% !important;
}


.modal_signup_background,
.modal_signup_dialog,
.dialog_wrapper {
    display: none !important;
}

/* resize Feed */
.ContentWrapper{
    max-width: 800px;
}

/* resize Discover New Spaces */
.main_feed .SuggestedUsersBundle, .main_feed .SuggestedTopicsBundle, .main_feed .SuggestedTribesBundle{
    width: 100%
}

/* change font */
* {
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
}

/* remove ad */
div.question_main_col > .UnifiedAnswerPagedList.PagedListFoo.unified {
    display: none !important;
}
}
