style.css:
@media(min-width: 1100px) {
    [class^="layout__SecondaryPrimaryColumnGrid"] {
        width: 100%;
        grid-template:"secondary primary" 100% / 340px;
        max-width: 85%;
    }
    [class^="layout__PrimaryColumn"] {
        max-width: inherit !important;
    }
    [class^="style__StickyHeaderContainer"] {
        max-width: 100% !important;
    }
    [class^="style__ThreadBackground"] {
        background: rgba(137, 137, 137, 0.71) none repeat scroll 0% 0% !important;
        transform: none !important;
        width: 100% !important;
        left: 0 !important;
    }
}
