style.css:
@-moz-document domain("optifine.net") {
/*@import url('https://fonts.googleapis.com/css?family=Raleway:400,600');*/ /*ree why cant i import fonts*/
* {
    font-family: 'Raleway', 'Helvetica', sans-serif; 
    font-weight: 400;
    color: #ddeaff;
    background-color: #21252d !Important;
    transition: 0.2s linear;
}
.tableNav {
    border: none;
    background-color: inherit;
}
A {
    transition: 0.2s ease-in-out;
}
A:hover {
    text-shadow: 0 0 2px #426aff;
    font-weight: lighter;
}
body {
    background-color: #1f1d23 !important;
    background-image: none;
}
.tableRoot {
    border:none;
}
}
