style.css:
@-moz-document domain("geoguessr.com") {

/* remove ads */
.no-pro .page, .no-pro .vertical-splitted-modal {padding: 0;}
.interstitial, .no-pro .vertical-splitted-modal .vertical-splitted-modal__right { display: none !important; }

/* set map zoom levels */

.guessmap.is-active.size-3 .guessmap-map {
    width: 3600px;
    height: 1800px;
}
.guessmap.is-active.size-2 .guessmap-map {
    width: 2560px;
    height: 1440px;
}
.guessmap.is-active.size-1 .guessmap-map {
    width: 1920px;
    height: 1080px;
}
.guessmap.is-active.size-0 .guessmap-map {
    width: 1280px;
    height: 720px;
}

/* move + - controls to the right */
.guessmap-controls-position {
    left:auto;
    right:0;
}

/* move + - controls to the bottom, this seems to make it a little buggy but helps if you have a small screen and zoomed in too far and the controls are off screen */
.guessmap-controls-position {
    top:auto;
    bottom:0;
}
.button--guess {
        margin-bottom:50px;
}
}
