	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


.sale-row__status-message {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/


selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.card-link,
.input,
.ngx-input-datepicker__btn,
.ngx-input-datepicker__input,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Misc _________________________________________________________________________*/

.sale-row__distance {
    color: #8afff0!important;
}
























}

@-moz-document domain("estatesales.net") {
