style.css:
@-moz-document url-prefix("https://mail.google.com") {
    body {
        background-color: #e5eaf1;
        padding-left: 20px;
        padding-right: 20px;
    }
    #guser, #gbar {}
    .th, .ts, .gb1, #guser .gb4 {
        color: black !important;
        text-decoration: none !important;
    }
    .ts:hover {
        color: #4c76b7;
    }
    .search-form-submit, input[name^=nvp_], input[name=file0], select[name*=act] {
        background-color: #6086bf;
        color: white;
        border-radius: 4px;
    }
    .search-form-submit:hover, input[name^=nvp_]:hover {
        box-shadow: none;
        background-color: #4c76b7;
    }
    .lb a, #gbar a {
        color: black !important;
        text-decoration: none !important;
    }
    a {
        text-decoration: none !important;
        color: black !important;
        cursor: pointer;
    }
    a:hover, #guser .gb4:hover, #gbar a:hover {
        color: #4c76b7 !important;
        text-decoration: underline !important;
    }
    .l td {
        font-size: 95%;
    }
    .l h2 a {
        color: #4c76b7 !important;
    }
    #sbq, .i, .mi {
        background-color: #ccd6e4;
        border: 1px solid #cecece;
        border-radius: 4px;
        padding: 4px;
    }
}
