style.css:
@-moz-document url-prefix("https://rtd.moe/translations/"), url-prefix("https://rtd.moe/shinka/"),  url-prefix("https://rtd.moe/motoyuusha/"),  url-prefix("https://rtd.moe/owmb/"),  url-prefix("https://rtd.moe/kumo-desu-ga/"),  url-prefix("https://rtd.moe/takami/"),  url-prefix("https://rtd.moe/oremegane/"),  url-prefix("https://rtd.moe/op-waifu/"),  url-prefix("https://rtd.moe/average-abilities/"),  url-prefix("https://rtd.moe/slave-harem/") {
article p {
    font-family: Helvetica, Arial, sans-serif;
    font-size: 22px;
    line-height: 32px;
    margin: -10px -10px 20px -10px;
    padding: 10px;
    border-radius: 5px;
    text-indent: 0;
}
html {
    scrollbar-color: #555555 #333333;
}
.html::-webkit-scrollbar-track-piece {
    background-color: #333333;
}
.html::-webkit-scrollbar-thumb:vertical {
    height: 30px;
    background-color: #555555;
}
h1, h2, h3, h4, h5, h6, h7, h8, h9 {
    color: #eeeeee;
}
.wp-post-navigation a {
    color:#eeeeee;
}
.single_post {
    box-shadow: none;
}
#amazon-polly-audio-table {
    display:none;
}
#page {
    margin: 0 auto;
    padding-top: 0;
}
div {
    background-color: #333333 !important;
    border-bottom-color: #333333;
    color: #eeeeee;
}
body {
    background-color: #333333;
    border-bottom-color: #333333;
    color: #eeeeee;
}
header {
    display:none;
}
a[class="skip-link screen-reader-text"] {
    display:none;
}
.article {
    float: none;
}
article {
    max-width:700px;
    width:700px;
    margin: 0 auto;
}
div[class="breadcrumb"] {
    display:none;
}
aside {
    display:none;
}
div[class="related-posts"] {
    display:none;
}
div[class="postauthor"] {
    display:none;
}
div[id="disqus_thread"] {
    display:none;
}
div[class="tags"] {
    display:none;
}
footer {
    display:none;
}
}
