style.css:
@-moz-document url("https://login.avans.nl/nidp/saml2/sso") {
    body,
    #Ecom_User_ID,
    #Ecom_Password,
    #loginButton2 {
        background-color: #333;
        color: #eee;
        transition: background-color 0.5s ease;
    }

    #Ecom_User_ID,
    #Ecom_Password {
        border-radius: 3px;
    }

    #Ecom_User_ID:focus,
    #Ecom_Password:focus {
        background-color: #622727;
    }

    #Ecom_User_ID:focus::placeholder,
    #Ecom_Password:focus::placeholder {
        color: #aaa;
    }

    #loginButton2:hover {
        background-color: #C6002A;
        color: white;
        border-color: white;
    }

    #loginButton2:active {
        transform: translateY(1px);
    }
}
