style.css:
@-moz-document regexp("https://kiwifarms.net/chat/general-chat.1/") {
.chat-user-avatar .avatar,
.chat-message-avatar .avatar {
    border-radius: 0px;
}
.chat-user-avatarBox{
    border: 2px solid grey;
}
.chat-user .chat-user-avatarBox::after {
    opacity: 0;
}
.chat-user.chat-user--active .chat-user-avatarBox {
    border: 2px solid green;
}
.chat-user.chat-user--idle .chat-user-avatarBox {
    border: 2px solid #ff0;
}
}
