@-moz-document url-prefix("https://www.nicovideo.jp/ranking") {
.RankingMainNicoad, .NicoadItem, .NicoadBlankItem, .LaneHeader_hotTopics, .VideosDecorationChunk:nth-child(n) > div:nth-child(n) > div:nth-child(7) {
  display: none;
}

.RankingMatrixNicoadsRow {
  display: none;
  position: fixed;  
}

.RankingMatrixVideosRow {
  margin-bottom: 30px !important;  
}

.RankingBaseItem {
  height: 228px;
}

.ToTopButton {
  left: 50% !important;
  opacity: 0.86;
}

.RankingMainContainer .ToTopButton {
  -webkit-transform: translateX(-24px);
  transform: translateX(-24px);
}

.HeaderContainer-ads{
  display: none;
}

.HeaderContainer-searchBox {
  justify-content: right;
}
}
