style.css:
/* i really want this to be global */

@-moz-document url-prefix("https://chuyencuadev.com/") {


*,
h1,
h2,
h3 {
	color: #bfbfbf;
}

body {
	background: #141414;
}


.posts .post {
	background-color: #1b1b1b;
}

.box-border-radius {
	border: 1px solid #5d5d5d;
}

#comments {
	border-top: 1px solid #5d5d5d;
	background: #1b1b1b;
}


#top-box,
#top-snippets-box {

	background: #1b1b1b;
}

.emoji-wysiwyg-editor {
	background: #1b1b1b;
	;
	color: #bfbfbf;
}

.fa-smile-o {
	color: white !important;
}
.form-input {
	background: #1b1b1b;
}

label {
	background: #1b1b1b;
	margin: 0 0.25rem;
}

}
