style.css:
@-moz-document domain("three-bas-examples.surge.sh") {
/* colors from https://colorhunt.co/palette/139181 */
/* Background */
body, nav, section, code {
    background-color: #010101 !important;
}

/* Text Color */
body, footer, section, h1, h2, h3, h4, h5, h6, span {
    color: #f0ece2 !important;
}

/* Secondary color */
a {
    color: #acdbdf !important;
}

/* Nav indicators */
.type-class {
    background: #69779b !important;
}
.type-function {
    background: #514949 !important;
}

/* Paramas Table row */
.params thead tr, .props thead tr {
    background: #010101 !important;
}

.params .name, .props .name, .name code {
    color: #d89191;
}
}
