style.css:
@font-face {
    font-family: MyCustomFont;
    src: local(Heiti TC), local("微软雅黑");
    /* no unicode-range; default to all characters */
}

/* Latin characters 專用 */
@font-face {
    font-family: MyCustomFont;
    /* 同樣的 font-family */
    unicode-range: U+00-024F;
    /* Latin, Latin1 Sup., Ext-A, Ext-B */
    src: local("Segoe UI"), /* OS X preferred */
    local("Segoe UI");
    /* Other OS */
}

/* 注音符號專用 */
@font-face {
    font-family: MyCustomFont;
    /* 同樣的 font-family */
    unicode-range: U+3100-312F;
    /* Bopomofo */
    src: local("浪漫雅圆"), /* OS X */
    local("浪漫雅圆");
    /* Windows Vista+ */
}

/* 日文假名專用 */
@font-face {
    font-family: MyCustomFont;
    /* 同樣的 font-family */
    unicode-range: U+3040-30FF;
    /* Hiragana, Katakana */
    src: local("Meiryo UI"), /* OS X */
    local("Meiryo UI");
    /* Windows Vista+ */
}

* {
    font-family: MyCustomFont, sans-serif;
}

a {
    font-family: MyCustomFont, sans-serif;
}
div {
    font-family: MyCustomFont, sans-serif;
}
