style.css:
@-moz-document domain("courses.nu.edu.eg") {
	.block, .eboxshadow, .loginbox, .textmenu, tr, #moodleheader, .dockedtitle, .paging-morelink a:hover {
		background-color: #242424!important
	}
	
	#page-content-wrapper {
		background-color: #1E1E1E!important
	}
	
	footer{
		display: none
	}
	
	section#page {
		padding-bottom: 0
	}
	
	h1, h2, h3, p, a, label, input, select, th, td, div, .error, .content,  .desc, .login {
		color: #cccccc!important
	}
	
	a:hover, .paging-morelink a:hover {
		color: #7777ff!important;
	}
	
	.panel-heading, input, select, .alert, .menu {
		background-color: #383838!important;
		border-color: #ffffff!important;
		border-width: 1px
	}
	
	.menu-action:hover{
		color: #000000!important;
	}
	
	.panel-body, .moodle-dialogue-content {
		background-color: #2C2C2C!important
	}
	
	.btn, .singlebutton input , .buttons input, input[type=submit ]{
		color: #000000 !important;
		background-color: #7777ff!important;
	}
	
	#moodleheader{
		background-image: none
	}
}
