style.css:
@-moz-document domain("google")
{
}

@-moz-document regexp("https?://(www|encrypted|images).google.*")
{
:root{--Logo:url(https://res.cloudinary.com/rama-cdn/image/upload/v1566122854/GooglePie2X_vmspac.png)no-repeat center}
:not(#⁠):not(#⁠):not(#⁠):not(#⁠) #logocont {
	padding-top: 0!important;
	margin-top: 0!important
}

:not(#⁠):not(#⁠):not(#⁠):not(#⁠) body.hp #hplogo,
:not(#⁠):not(#⁠):not(#⁠):not(#⁠) body.des-mat #lga>img {
	display: none!important
}

:not(#⁠):not(#⁠):not(#⁠):not(#⁠) #logo>*,
:not(#⁠):not(#⁠):not(#⁠):not(#⁠) #logocont>* {
	opacity: 0!important
}

:not(#⁠):not(#⁠):not(#⁠):not(#⁠) body.hp #lga,
:not(#⁠):not(#⁠):not(#⁠):not(#⁠) body.des-mat #lga {
	background: var(--Logo)!important;
}

:not(#⁠):not(#⁠):not(#⁠):not(#⁠) #logo,
:not(#⁠):not(#⁠):not(#⁠):not(#⁠) #logocont {
	background: var(--Logo)!important;
	background-size: 92px auto!important;
}
}
