style.css:
@-moz-document url-prefix("https://thetrove.net") {
body {
    background-color: #353535;
    color: #ac6741;
}

.listing-table th,
.listing-table td {
    border-color: #988340;
}

a {
    color: #dedede;
    font-family: cursive;
    font-weight: bolder;
}

.breadcrumbs {
    background-color: #464646;
}

.litem .litem_name a {
    color: #b78000;
}

.litem .litem_modified,
.litem .litem_size {
    opacity: .6;
    font-size: .85em;
    color: #6cb760;
    font-weight: bolder;
}

.pure-button {
    background-color: #464646;
}

header {
    background-color: #464646;
}

#menu {
    background-color: #464646;
}

#handle {
    color: #63798b;
}

#menu .pure-menu li a {
    color: #b78011;
}

.listing-table thead {
    background-color: transparent;
    border-top: 0;
    color: white;
}

header .header_search .pure-form input {
    background-color: #b1b1b1;
}

}
