style.css:
@-moz-document domain("wx.qq.com")
{
/* 
Wechat Web User Interface
 Author: Basson
 Version: 1.0.1
 Update Date: 2019/08/23
*/

/* change backgroudn */
body { background:#2e3641; }
#chatArea { background:none; }

/* change the text color */
.box_hd .title .title_name  {color: #efefef; }
.message .bubble,
.chat .box_ft .content .flex {color: #444; }
.message_system .content { color:#444 }
.chat_item .info .nickname_count,
.chat_item .info .nickname_text,
.chat_item .ext { color:#333; }
.chat_item .info .msg,
.chat_item.active .ext, 
.chat_item.active .info .msg,
.header .info .nickname .display_name{ color: #333; }
.msg { display:none; }
.message_empty { color:#555 }

/* change the bubble color */
.bubble.bubble_primary.right.arrow_primary:before, 
.bubble.bubble_primary.right:after { border-left-color: #444; }
.chat .box_ft { border-color:#323232 }
.bubble.left:after { border-right-color: #444; }
.bubble.bubble_primary { background:none; }
.bubble.bubble_default { background:none; }

/* hide the copyrights */
.main .copyright { display:none; }

/* hide the avatar */
.header .avatar,
.chat_item .avatar,
.message .avatar { display:none; }

/* hide emoji */
.nickname_text .emoji { display:none; }

/* hide the download ads */
.download_entry { display:none; }
.panel.give_me .nav_view { top:0; }

/* hide some buttons */
.search_bar,
.header,
.tab,
#navView,
.box_hd .title_wrap,
.web_wechat_down_icon,
.action,
.web_wechat_screencut, 
.web_wechat_pic, .web_wechat_profile_loc { display:none; }
}
