.tw-root--theme-dark .navigation-link {
	color: #555;   
}

.tw-c-text-alt {
	color: #999!important;
}

.tw-button-icon {
 	color: #555;  
}

.tw-interactable {
 	color: #555;   
}

.tw-c-text-inherit {
 	color: #555;   
}

.tw-core-button--primary {
 	color: #333!important;   
}

.tw-core-button--secondary {
 	color: #333!important;   
}

body {
 	color: #555!important;   
}

.tw-c-text-base {
 	color: #555!important;   
}

.tw-input {
 	color: #555;   
}

input::placeholder {
 	color: #333;   
}

.tw-select-button {
 	color: #555;
}

.tw-pill {
 	color: #999;   
}

