style.css:
@-moz-document domain("twitch.tv") {
/**                                                     **/
/** Twitch Dark Style v1.1 by Strong_WTF **/
/**                                                     **/

:root {
    --purple: #b391e6 !important;
    --darkpurple: #4b367b !important;
    --purple1: #6d49a2 !important;
    --purple2: #8d65c7 !important;
    --header: #19171c !important;
    --greybg: #19171c !important;
    --midgrey: #0f0f11 !important;
}

.tw-root--theme-dark {
    --color-background-base: #1d1b21;
    --color-background-alt: #212126;
    --color-fill-brand: #62458c;
    --color-text-link: #8d65c7;
    --color-background-body: #0f0f11;
    --color-background-button-overlay-primary-default: #292929;
    --color-text-button-overlay-primary: #ccc;
    --color-background-button-overlay-primary-active: #292929;
    --color-background-button-overlay-primary-hover: #333;
}

body {
    font-family: helvetica, sans-serif!important;
    --color-text-tooltip-alt: hsla(0, 0%, 55%, 0.8);
    --color-text-tooltip-alt-2: hsla(0, 0%, 55%, 0.8);
}

/* fix avatar */
.tw-c-background-base {
    background-color: unset!important;
}

.btDAGg {
    background-color: unset;
}

/* button */
.jCxFvU {
    background-color: var(--purple1);
}

.jeBpig {
    background-color: var(--purple1);
}

.tw-root--hover .jCxFvU:hover {
    background-color: var(--purple2);
}

.tw-root--hover .jeBpig:hover {
    background-color: var(--purple2);
}

.tw-root--hover .jCxFvU:active:hover:active {
    background-color: var(--purple1);
}

.tw-root--hover .jeBpig:active:hover:active {
    background-color: var(--purple1);
}

.tw-button {
    background-color: var(--purple1);
}

.tw-button:focus, .tw-root--hover .tw-button:hover:not(.tw-button--disabled) {
    background-color: var(--purple2);
    color: #1c1c1c;
}

.tw-button:active:not(.tw-button--disabled), .tw-button:hover:active:not(.tw-button--disabled) {
    background-color: var(--purple2);
}

.tw-button--success {
    color: #1c1c1c;
}

.eJWTAW {
    background-color: var(--purple1);
}

.tw-root--hover .eJWTAW:hover {
    background-color: var(--purple2);
}

.tw-root--hover .eJWTAW:active:hover:active {
    background-color: var(--purple1);
}

.tw-toggle__button {
    background-color: #131313;
}

.ScToggleInput-sc-796zbf-1:checked + .ieuPHF {
    border-color: var(--purple2);
}

.tw-toggle__input:checked+.tw-toggle__button:before {
    border-bottom: 2px solid var(--purple2);
    border-left: 2px solid var(--purple2);
}

.tw-toggle__input:checked+.tw-toggle__button:after {
    background-color: var(--purple2);
}

/* text */
.tw-font-size-2 {
    font-size: 3.2rem!important;
}

.tw-font-size-4 {
    font-size: 1.6rem!important;
    font-weight: 100;
}

.tw-semibold, .tw-strong {
    font-weight: 100!important;
}

.tw-bold {
    font-weight: 100!important;
}

h1 {
    font-size: 3.2rem;
}

h2 {
    font-size: 2.6rem;
}

a {
    color: var(--purple);
}

/* main */
.tw-root--theme-dark body {
    background-color: var(--midgrey)!important;
}

.top-nav {
    background-color: var(--darkpurple)!important;
}

.tw-root--theme-dark .chat-room {
    background: var(--midgrey)!important;
}

.tw-root--theme-dark .side-nav__overlay-wrapper {
    background-color: var(--header)!important;
}

.tw-root--theme-dark .side-nav {
    border-right: 1px solid #242426!important;
}

.tw-elevation-1 {
    box-shadow: 0px 0px 0px rgba(0,0,0,.65),0px 0px 8px rgba(0,0,0,.4)!important;
}

/* title */
.tw-title {
    margin-bottom: 4px!important;
}

.tw-border-t {
    background: var(--header);
}

.tw-mg-r-1 {
    margin-right: .6rem!important;
}

.tw-word-break-word {
    margin-bottom: 1px;
}

.metadata-layout__support {
    margin: -2px 0px 9px 0px;
}

.channel-info-content .tw-accent-region, .channel-info-content .gocjHQ {
    margin: 0px 0 -14px 0;
}

/* border */
.side-nav-header {
    border-bottom: 1px solid #242426;
    padding-bottom: 10px;
}

.tw-border-l {
    border-left: 1px solid #242426!important;
}

.tw-border-b {
    border-bottom: 1px solid #242426!important;
}

.tw-border-r {
    border-right: 1px solid #242426!important;
}

.tw-border-t {
    border-top: 1px solid #242426!important;
}

.tw-border {
    border: 1px solid #242426!important;
}

/* channel */
.channel-root--home .channel-info-content {
    width: calc(100%);
    margin-top: 20px;
}

.home-header-sticky {
    background: var(--header);
    width: calc(100%);
    padding: 0 40px 0 40px;
}

.tw-align-items-center.tw-c-background-base.tw-c-text-alt.tw-elevation-1.tw-flex.tw-full-height.tw-full-width.tw-overflow-hidden.tw-relative.tw-z-default {
    background-color: var(--header)!important;
}

.home-offline-hero__upsell {
    background-color: var(--color-background-base)!important;
}

/* directory */
.tw-root--theme-dark .directory-header-new__banner-cover {
    background: linear-gradient(0deg,#0f0f11,rgba(0,0,0,.25)),linear-gradient(90deg,#444,rgba(0,0,0,.25))!important;
    width: 104%;
    margin-left: -30px;
}

.directory-header-new__info {
    margin-left: 30px;
}

.tw-tag__content {
    padding: 0 .6rem;
}

/* sub stream */
.tw-root--theme-dark .subscriber-stream-tag {
    background: var(--purple1);
}

.tw-root--theme-dark .subscriber-stream-tag:focus, .tw-root--theme-dark .subscriber-stream-tag:hover {
    background: var(--purple2);
}

/* bits */
.t-bits-card__footer, .tw-root--theme-dark .t-bits-card__footer, .tw-root--theme-light .t-bits-card__footer {
    background-color: var(--darkpurple);
}

.bits-leaderboard-expanded__section-break {
    border-bottom: 1px solid var(--purple1);
}

.bits-card-wrapper {
    background-color: var(--color-background-base)!important;
}

/* host */
.tw-c-background-accent {
    background-color: #4e357b!important;
}

.tw-c-background-accent-alt-2 {
    background-color: var(--purple1)!important;
}

/* multistream */
.squad-stream-top-bar__container {
    background-color: var(--color-background-base)!important;
}

.multi-stream-player-layout__player--outlined:not(.multi-stream-player-layout__player--ad-playing)::after, .multi-stream-player-layout__player:not(.multi-stream-player-layout__player--ad-playing):hover::after {
    border: 3px solid var(--purple1);
}

.squad-stream-channel-switcher__btn--active, .squad-stream-channel-switcher__btn--active:hover {
    box-shadow: 0 0 0 2px var(--purple1);
}

/* chat */
.tw-textarea, .dTtjBt {
    background-color: var(--midgrey);
}

.dTtjBt:hover {
    background-color: hsl(0deg 0% 18%);
}

.tw-textarea:focus, .dTtjBt:focus, .dTtjBt:focus:hover {
    background-color: #222;
    border-color: var(--purple1);
}

.ffz-input:focus, .ffz-input:focus:hover, .ffz-select:focus, .ffz-select:focus:hover, .ffz-textarea:focus, .ffz-textarea:focus:hover {
    background-color: #222;
    border-color: var(--purple1);
}

.tw-pd-x-05 {
    padding: 3px 4px 4px 4px;
}

.ffz-balloon {
    background-color: var(--color-background-base)!important;
}

.stream-chat-header {
    background-color: var(--color-background-base)!important;
}

.channel-leaderboard {
    background-color: var(--header)!important;
    height: unset;
}

.bits-leaderboard-expanded__header {
    background-color: var(--color-background-base)!important;
}

.channel-leaderboard-header-rotating {
    background-color: var(--color-background-base)!important;
}

.chat-input-container__open {
    background-color: var(--color-background-base)!important;
}

.chat-input-tray__open {
    background-color: var(--color-background-base)!important;
    border: 1px solid hsla(0,0%,100%,.1)!important;
}

.chat-input-tray__floating {
    background-color: var(--color-background-base)!important;
    border: 1px solid hsla(0,0%,100%,.1)!important;
}

.chat-line__reply-icon {
    background-color: var(--color-background-base)!important;
}

.tw-mg-1.tw-border-t.tw-pd-t-1.tw-mg-b-0 {
    background-color: var(--color-background-alt)!important;
}

.viewer-card {
    background-color: var(--header)!important;
}

.viewer-card-mod-drawer-tabs {
    background-color: var(--color-background-alt)!important;
}

/* notification */
.onsite-notification-toast {
    background-color: var(--color-background-base)!important;
}

/* whispers */
.tw-balloon {
    background-color: var(--header)!important;
}

.whispers-thread {
    background-color: var(--header)!important;
}

/* search */
.tw-elevation-3 {
    background-color: var(--header)!important;
}

/* moderator mode */
.mod-view-panel-header {
    background-color: var(--header)!important;
}

.modview-dock {
    background-color: var(--header)!important;
}

.mod-view-context-bar__content {
    background-color: var(--header)!important;
}

/* wallet */
.payment-methods-tab.tw-border-b.tw-border-l.tw-border-r.tw-border-radius-medium.tw-border-t.tw-c-background-base.tw-mg-b-2 {
    background-color: var(--header)!important;
}

.tw-flex.tw-flex-column.tw-justify-content-between.tw-pd-x-2.tw-pd-y-1 {
    background-color: var(--header)!important;
}

.tw-border-b.tw-border-l.tw-border-r.tw-border-t.tw-c-background-base.tw-pd-x-2.tw-pd-y-1 {
    background-color: var(--header)!important;
}

.tw-table-body {
    background-color: var(--header)!important;
}

.tw-border-b.tw-border-l.tw-border-r.tw-border-t.tw-c-background-base {
    background-color: var(--header)!important;
}


/* dashboard main */
.sunlight-top-nav {
    background-color: var(--color-background-base)!important;
}

.stream-manager--page-view .mosaic-root {
    background: #151515;
}

.tw-modal {
    background-color: var(--header)!important;
}

.bgVzod.tw-core-button {
    background-color: var(--purple1);
}

.tw-root--hover .bgVzod:active:hover:active {
    background-color: var(--purple1);
}

.tw-root--hover .bgVzod:hover {
    background-color: var(--purple2);
}

.quick-action-layout {
    background-color: var(--color-fill-brand);
}

.quick-action__not-edit-mode:not([disabled]):focus, .quick-action__not-edit-mode:not([disabled]):hover, .tw-root--theme-dark .quick-action__not-edit-mode:not([disabled]):focus, .tw-root--theme-dark .quick-action__not-edit-mode:not([disabled]):hover, .tw-root--theme-light .quick-action__not-edit-mode:not([disabled]):focus, .tw-root--theme-light .quick-action__not-edit-mode:not([disabled]):hover {
    background: var(--purple2);
}

.mosaic-split:active, .mosaic-split:hover {
    background-color: var(--purple1);
}

.iwbBQp { /* panel button */
    background-color: var(--purple1);
}

.tw-root--hover .iwbBQp:hover {
    background-color: var(--purple2);
}

.tw-c-background-alt-2 {
    background-color: #212121!important;
}

.tw-root--theme-light.tw-c-background-base {
    background-color: var(--color-background-base)!important;
}

/* dashboard analytics */
.nav-bar-layout__height {
    background-color: var(--header)!important;
}

.tw-c-background-base.tw-elevation-1.tw-mg-b-2 {
    background-color: var(--header)!important;
}

.tw-border-radius-large.tw-c-background-base.tw-elevation-1 {
    background-color: var(--header)!important;
}

/* dashboard achievements */
.ach-q-card {
    background-color: var(--header)!important;
    box-shadow: #6747a4 0px 0px 0px 1px;
}

.tw-badge--dashboard {
    background-color: var(--purple1);
}

.tw-progress-bar__fill {
    background-color: var(--purple1);
}

.tw-root--theme-dark .ach-card--expanded .ach-card__inner, .tw-root--theme-light .ach-card--expanded .ach-card__inner {
    border-color: var(--purple1);
}

.tw-root--hover .hZEZfD:active:hover:active {
    color: #fff;
}

.tw-root--hover .hZEZfD:hover {
    color: #fff;
}

.tw-root--hover .jVNPSu:active:hover:active {
    color: #ddd;
}

.tw-root--hover .jVNPSu:hover {
    color: #ddd;
}

/* dashboard activity */
.activity-page {
    background-color: var(--header)!important;
}

/* dashboard video-producer */
.tw-root--theme-dark .video-card {
    background-color: var(--header)!important;
}

/* dashboard stream */
.tw-border-b.tw-border-bottom-left-radius-medium.tw-border-bottom-right-radius-medium.tw-border-top-left-radius-medium.tw-border-top-right-radius-medium.tw-border-l.tw-border-r.tw-border-t.tw-c-background-base.tw-mg-b-4 {
    background-color: var(--header)!important;
}

.tw-border-b.tw-border-l.tw-border-r.tw-border-radius-large.tw-border-t.tw-c-background-base.tw-mg-b-4 {
    background-color: var(--header)!important;
}

/* dashboard channel-points */
.channel-points-rewards-list {
    background-color: var(--header)!important;
}

/* dashboard drops */
.tw-border-b.tw-border-l.tw-border-r.tw-border-radius-medium.tw-border-t.tw-c-background-base.tw-flex.tw-mg-b-4.tw-pd-2 {
    background-color: var(--header)!important;
}

.accordion-header__contents {
    background-color: var(--header)!important;
}

.tw-block.tw-c-background-base.tw-elevation-1.tw-pd-2 {
    background-color: var(--color-background-base)!important;
}

/* dashboard broadcast */
.tw-c-background-base.tw-elevation-1.tw-flex.tw-flex-column.tw-full-height.tw-pd-2 {
    background-color: var(--header)!important;
}

/* dashboard extensions */
.tw-border-radius-large.tw-c-background-alt.tw-elevation-2.tw-full-height {
    background-color: var(--header)!important;
}

.tw-align-center.tw-align-items-center.tw-border-radius-large.tw-c-background-alt.tw-elevation-2.tw-flex.tw-flex-column.tw-justify-content-between.tw-mg-1.tw-pd-x-1.tw-pd-y-2 {
    background-color: var(--header)!important;
}

.media-carousel__button {
    background-color: var(--color-background-base)!important;
}

.tw-border-b.tw-border-l.tw-border-r.tw-border-radius-large.tw-border-t.tw-c-background-base {
    background-color: var(--header)!important;
}


/* subscriptions */
.sub-modal {
    background-color: var(--color-background-base)!important;
}

.tier-display {
    color: var(--purple2);
}

.sub-badge-progress__progress-bar .tw-progress-bar div, .tw-root--theme-dark .sub-badge-progress__progress-bar .tw-progress-bar div, .tw-root--theme-light .sub-badge-progress__progress-bar .tw-progress-bar div {
    background: var(--purple2);
}

.tw-border-radius-medium.tw-c-background-base.tw-elevation-1.tw-pd-b-05.tw-pd-l-1.tw-pd-r-1.tw-pd-t-05.tw-relative {
    background-color: var(--color-background-base)!important;
}

.summary-layout {
    background-color: var(--header)!important;
}

.minimal-top-nav {
    background-color: var(--color-background-base)!important;
}

.minimal-top-nav__dropdown-open {
    background-color: var(--color-background-base)!important;
}

.tw-pd-2.tw-xs-pd-x-3.tw-xs-pd-y-2 {
    background-color: var(--color-background-base)!important;
}

/* twitch player */
.tw-c-background-overlay {
    background-color: rgba(25, 25, 25, 0.6)!important;
}

.offline-recommendations-video-card .offline-recommendations-video-card-border-3, .tw-root--theme-dark .offline-recommendations-video-card .offline-recommendations-video-card-border-3, .tw-root--theme-light .offline-recommendations-video-card .offline-recommendations-video-card-border-3 {
    border: 1px solid var(--purple1);
}

/* VOD */
.qa-vod-chat {
    background-color: var(--midgrey);
}

/* clips main */
.tw-root--theme-dark .clips-top-nav-user {
    background-color: var(--color-background-base)!important;
}

.tw-root--theme-dark .clips-top-nav {
    background-color: var(--color-background-base)!important;
}

.clips-sidebar {
    background-color: var(--header);
}

.clips-top-nav-user__dropdown--open {
    background-color: var(--color-background-base)!important;
}

/* team (group) */
.teams-player {
    background-color: var(--color-background-base)!important;
}

.member-list__scrollable-container {
    background-color: var(--color-background-base)!important;
}

/*.tw-card-img--size-4 {
    margin-right: 8px;
}*/

/* drops */
.drops-campaign-details__drops-success, .tw-root--theme-dark .drops-campaign-details__drops-success, .tw-root--theme-light .drops-campaign-details__drops-success {
    color: var(--purple2);
}

/* social media */
.social-media-space--content {
    background-color: var(--header)!important;
}

.social-media-link {
    width: 125%;
}



/* ffz */
.ffz__tooltip {
    background: #242426;
    color: #ccc;
}

.ffz__tooltip .ffz__tooltip--arrow {
    background: #242426;
}

.ffz-tooltip.ffz-tooltip--no-mouse>* {
    padding: 4px 0px 3.1px 0px;
}

.ffz--meta-tray {
    width: 460px;
    margin-top: 14px;
}

.ffz--reply-mention:hover, .ffz--reply-mention:focus {
    background-color: var(--purple1);
}

.ffz-stat--fix-padding {
    margin-top: -.5rem!important;
    margin-bottom: -.7rem!important;
}

/* bttv */
.bttv-moderator-card-user-stats {
    padding-bottom: 3px;
    padding-top: 7px;
}

.tw-stat__value {
    margin-bottom: 4px;
}

/* selection */
::selection {
    background-color: #be93ff!important;
}

/* tooltip */
.tw-tooltip, .tw-tooltip-popper-arrow:after, .tw-tooltip:after {
    background-color: #242426;
    color: #ccc!important;
}

.tw-tooltip-wrapper {
    background-color: #242426;
    color: #ccc!important;
}

/* Copyright */
li.Footer-item.Footer-copyright.copyright:after {
  content: " | Dark Style by @Strong_WTF"
}
}
