style.css:
@-moz-document domain(web.whatsapp.com) {
div[aria-label="Message list. Press right arrow key on a message to open message context menu."] { background-color:#eee !important; }
.message-out div:first-of-type { background-color: #ddd !important; }
.message-out span[data-icon="tail-out"] { color:#ddd !important; }
._1wQjq, ._3omcs { background-image:none !important; }
body,img,video,.video-thumb,.emojik,._1Bw-K,._1iCCh,._1e2CU,.tXOSv,div[data-animate-status-v3-modal-background=true],
div.landing-window { filter:invert(95%) !important; }
div.landing-window img { filter:invert(0%) !important; }
}
