style.css:
@-moz-document domain("focusmusic.fm") {
.night-mode{
    background-color: #112137;
}

.night-mode h2{
    color: #ace3fb;
}

.night-mode .site-container{
    filter: none;
}

.night-mode .footer-controls > .btn{
    background-color: #307fe2;
}

.night-mode .footer-controls .fa{
    color: black;
}

.night-mode .footer-controls .btn:hover .fa{
    color: white;
}

.night-mode .footer-icon .fa{
    color: #ace3fb
}

.night-mode .footer-icon .btn:hover .fa{
    color: white;
}
}
