style.css:
@-moz-document domain("freestyler.ws") {
body {
	overflow : hidden;
}
body:before {
	background-color : GREEN;
	bottom           : 0;
	color            : PINK;
	content          : 'freestyler.ws - sucks and ignore all my emails due to script deletion';
	font-family      : sans-serif;
	font-weight      : 900;
	font-style       : oblique;
	font-size        : 19vh;
	padding          : 0px;
	padding-left     : 3vw;
	top              : 0;
	left             : 0;
	line-height      : 1em;
	position         : fixed;
	right            : 0;
	z-index          : 10000;
}
@media screen and (min-width: 1025px) {
	body:before {
		padding-left : 7vw;
	}
}
}
