style.css:
@-moz-document url-prefix("https://beta.todoist.com/app"), url-prefix("https://todoist.com/app")
{
  
/*LEFT MENU***************************************************************/
/*TOP FILTERS SECTION-REDUCE VERTICAL SPACING*/
.left_menu li {
  padding-top: unset !important;
  padding-bottom: unset !important;
}

/*PROJECTS SECTION-REDUCE VERTICAL SPACING*/
.left_menu td {
  padding-top: 4px!important;
  padding-bottom: 4px!important;
}

/*PROJECT LIST DROP DOWN ARROWS-VERTICALLY ALIGN (OFF FROM REDUCING VERTICAL SPACING ABOVE)*/
.generic_left_list .arrow {
	margin-top: 2px!important;
}

/*PROJECT DRAG & DROP DROP HANDLES-VERTICALLY ALIGN (OFF FROM REDUCING VERTICAL SPACING ABOVE)*/
.generic_left_list .drag_and_drop_handler{
	padding-top: 10px;
}

/*LABELS & FILTERS DRAG & DROP HANDLES- VERTICALLY ALIGN*/
.dnd_handle {
  padding-top: 0px!important;
}

/*HIDE "ARCHIVED PROJECTS", "ADD PROJECT", REMOVE BOTTOM PADDING*/
#projects_archived_holder {
	display: none;
}
.left_list_man {
	display: none!important;
}
.left_list_manager_holder {
	padding-bottom: unset!important;
}


/*LEFT MENU (REMOVE UNNECESSARY COUNTERS)**********************************/
/*REMOVE PROJECT COUNTERS*/
/*PROJECT COUNTERS-HIDE*/
.counter_count {
display: none!important;
}

/*TOP SECTION COUNTERS- SHOW INBOX & TODAY*/
.filter .item_counter {
display: inline-block!important;
}

/*TOP SECTION COUNTERS-HIDE REST*/
.item_counter {
display: none!important;
}

/*MAIN PANEL **************************************************************/
/*LEFT ALIGN CONTENT*/
#content {
	justify-content: left!important;
}
.upcoming_view__list__wrapper {
	justify-content: left!important;
}

/*SIDE PADDING 55 TO 42*/
#editor {
	padding: 80px 42px 84px!important;
}
.upcoming_view__list {
	padding: 0 42px!important;
}

/*ADJUST R MARGIN TO 0*/
.task_list_item__content {
	margin-right: 0px!important;
}

/*MOVE DUE DATES & NOTES TO MAIN ROW R OF TASK TEXT AND MAKE ADJUSTABLE*/
.task_list_item__content {
	display: flex!important;
}
.task_content {
	flex:1;
}

/*ALIGN DUE DATE & R ALIGN PROJECTS*/
.task_list_item__info_tags .task_list_item__project {
  margin-left: auto;
  margin-right: 0px;
}
.task_list_item__info_tags {
 Width: 250px!important;
}

/*MAIN PANEL (PROJECTS & INBOX)*********************************************/
/*PROJECT HEADER-REDUCE SPACING (MARGIN)*/
.view_header {
margin-bottom: 0px!important;
}

/*TASKS-REDUCE SPACING (PADDING)*/
.task_item_details {
padding-top: 1px!important;
padding-bottom: 1px!important;
}

/*TASK DROP DOWN ARROW-VERTICALLY ALIGN (OFF FROM SPACING ADJUSTMENT ABOVE)*/
.task_item .arrow {
  margin-top: 1px!important;
  }

/*SECTION HEADER-REDUCE SPACING (PADDING,MARGIN)*/
.section_list>li {
    margin-top: 0px!important;
    margin-bottom: 0px!important;
    padding-bottom: 0px!important;
}

/*PROJECT HEADER-REDUCE SPACING (MARGIN) */
.project_editor_instance {
    margin-bottom: 0px!important;
}

/*COMPLETED TASKS-REDUCE SPACING*/
.moreItemsHint {
  padding-top: 0px!important;
  padding-bottom: 0px!important;
  font-style: italic;
}

/*TASK & SECTION DRAG & DROP HANDLES*/
.drag_and_drop_handler{
    margin-top:-8px!important;
}
.section header>.drag_and_drop_handler{
    margin-top: 0px!important;
}

/*REMOVE "ADD TASK" IN EACH SECTION*/
#editor .controller {
    display: none
}

/*REMOVE "ADD SECTION" HOVER*/
.section_list .hover_action_button {
    display: none;
}

/*REMOVE "TASK ACTIONS" HOVER ON RIGHT*/ 
.task_item  .task_item_actions {
  display: none;
}


/*MAIN PANEL (TODAY PAGE)***************************************************/
/*TASKS-REDUCE SPACING*/
.task_item .task_item_details .task_item_details_bottom {
margin-top: 0px;
}

/*ALIGN CHECKBOX, ARROW, DRAG HANDLE*/
.task_list_item .item_checkbox {
  margin: 3px 15px 0 0
}
.task_list_item .arrow {
  margin-top: 2px!important;
}
.task_list_item .task_list_item__drag_container {
  margin-top: -6px!important;
}

/*REMOVE "ADD TASK" IN EACH SECTION*/
.task_actions {
    display: none
}

/*REMOVE "TASK ACTIONS" HOVER ON RIGHT*/
.task_list_item__actions {
  display: none!important;
}


/*MAIN PANEL (UPCOMING PAGE)************************************************/
/*TASKS-REDUCE SPACING*/
.task_list_item .task_list_item__content {
padding: 0px!Important;
}
.task_list_item__info_tags {
margin: 0px;
}

/*SECTIONS-REDUCE SPACING*/
.upcoming_view .upcoming_view__list .section__wrapper {
    padding-top: 10px!important;
}

/*REMOVE "ADD TASK" IN EACH SECTION*/
.agenda_add_task {
    display: none
}

/*HIDE CALENDAR ICON*/
.task_list_item__info_tags .date .calendar_icon {
    display: none;
}

/*VERTICALLY ALIGN LABEL*/
.task_list_item__info_tags .task_list_item__info_tags__label {
    margin-bottom: 3px!important;
}

/*VERTICALLY ALIGN NOTES ICON*/
.task_list_item__info_tags .notes_icon {
    margin-top: 2px!important;
}

/**MAIN PANEL POPUP WINDOW- ALIGN TEXT WIDTH**/
.reactist_modal_box.detail_modal .reactist_modal_box__body .task_list_item__info_tags {
 Width: 75px!important;
}

}
