style.css:
@-moz-document domain("tweetdeck.twitter.com"), domain("twitter.com"), domain("mobile.twitter.com") {
* :not(li):not(a):not(.tweet):not(.ProfileHeading-content):not(.spinner), .btn, .dropdown-toggle:before, .ProfileCard-avatarLink {
  border-radius: 0px !important;
}
.r-sdzlij {
  border-radius: 0px!important;
}
.DashboardProfileCard-avatarImage {
    border-radius: 0px;
    border: none;
    color: #0000;
}
.DashboardProfileCard-avatarLink {
    background-color: #0000;
    border-radius: 0px;
}
.btn {
    background-color: #0000;
    background-image: none;
}
.ProfileAvatar {
    background: #0000;
    border: none;
    border-radius: 0px;
}
html.dark .avatar-border--2 {
    border: none;
    background-color: #0000;
}
.r-11mg6pl, .r-1xc7w19, .r-1twgtwe, .r-f6ebdl {
    border: none;
}

.Gallery-media img, a[href$="/photo"], html.dark .compose-account-img {
    background-color: #0000;

}
html.dark body {
    background-color: #1c2938;
}
.Gallery-content {
    background-color: #0000;
    border-radius: 7px;
}
.Gallery-media {
    background: #0000;
    border-radius: 0;
}
}
