style.css:
@-moz-document domain("habr.com") {
.layout, .layout__row_navbar, .nav-links__item-link, .layout__row_navbar, .layout__row_footer-links, .layout__row_navbar, .default-block {
    background: #222222;
}
.post__title-text, .post__text-html, .post-info__title, .comment__message, .comment-form__preview, .post__title_link, .news-topic__title {
    color: #dcdcdc;
}
.icon-svg_logo-habrahabr, .default-block__header-title {
    color: #5d9cec;
}
.post-info__title:visited, .post-info__title-link:visited {
    color: #e4e8ea;
}
.megapost-teasers {
    display: none;
}
}
