style.css:
@-moz-document domain("katowicedzis.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #00AFF2 #1c1c1c;
}
/*HEADER*/
.site-header{
    background: #3b3b3b;
}
#site-navigation{
    background: #505050;
}
.navbar-default .navbar-nav > li > a{
    color: #fff;
}
.navbar-default .navbar-nav > .active > a, .navbar-default .navbar-nav > .active > a:hover, .navbar-default .navbar-nav > .active > a:focus{
    color: #00AFF2;
}
input#s{
    background: #1c1c1c;
    border-color: #00AFF2;
    color: #fff;
}
.top-search-box .searchform *{
    margin-right: 15px;
}
#site-navigation.shrink{
    background: #505050;
}
/*HEADER*/
/*STRONA-GLOWNA*/
.main-container{
    background: transparent;
}
.news-item h2 a,.post-excerpt,.author-meta a,.author-meta span,nav.navigation.pagination .nav-links a{
    color: #fff;
}
/*STRONA-GLOWNA*/
/*ARTYKUL*/
.single-title{
    color: #00AFF2;
}
.single-entry-summary p{
    color: #fff;
}
.page-title{
    color: #00AFF2;
}
/*ARTYKUL*/
.entry-content h3{
    color: #fff;
}
/*FOOTER*/
.footer-credits-text.text-center:after{
    content: " | Ciemny motyw by Maciejozaman";
}
/*FOOTER*/
}
