style.css:
@-moz-document domain(dbdiagram.io) {
#chart{
    background: #333;
}

.field rect{
    fill: #212529;
}

.primary-key .field-name{
    fill: #fff;
}

#input-container{
    background: #3e3d3d;
    color: #ccc;
}

.btn{
    color: #ccc;
}

.slider-toggle-container #toggle-container .slider{
background-color: #575353;
}

.slider-toggle-container #toggle-container .slider:before{
    background-color: #ccc;
}

#zoom-scale-slider{
    opacity: 0.5;
}

.field.highlight-field,
.highlight-field rect{
 fill: #000;
}

.field.highlight-field .field-name,
.highlight-field rect  .field-name
{
    color:#eee;
}

.endpoint:not(.hidden){
fill: #eee;
}

}/* END @-moz-document domain */
