style.css:
@-moz-document url-prefix("https://www.thecombineforum.com/threads/") {
.message {
    margin-bottom: 20px;
    border: 1px solid #686c6e;
    /*padding: 5px;
    border-radius: 5px;*/
    margin-left: 5px;
}

.california-thread-ad {
    border-top: none;
}

.california-banner-ad-container {
    padding-bottom: 0px;
    border: none;
}

.block--messages .message {
    background-color: #7b0000;    
}

.message-cell.california-message-cell {
    padding-left: 10px;
    background-color: #272a2f;
}

.message-cell.message-cell--user {
    background-color: #444444;
}

.p-pageWrapper {
    background-color: #900;
}

.california-thread-sidebar {
    flex: 0 0 100px;
}

.sidebar {
    margin-left: 15px;
    width: 100px;
}

.california-sidebar-ad-sticky {
    visibility: collapse;
}

#RightRail_Vendor {
    visibility: collapse;
}

.california-popular-communities-container{
    visibility: collapse;
}
}
