style.css:
@-moz-document regexp(".*app.box.com.*") {
@media (prefers-color-scheme: dark){
   *:not([data-portal]) {
        background-color: #202020 !important;
        color: #AAA !important;
        border:none !important;
   }
   iframe:not([data-portal]) {
        background:white !important;
   }
   .logo {
        filter: grayscale(1) invert(1) !important;
        mix-blend-mode: screen;
        background-color: transparent !important;
    }
    .item-icon, .left-sidebar-icon-wrapper {
        filter: grayscale(1) brightness(.5) !important;
        background-color:transparent !important;
    }
    .modal-dialog, .btn {
        border:1px solid #555 !important;
    }
    .is-selected:not(.tab-panel) {
        background:rgba(20,20,50,0.5) !important;
    }
    .is-selected * {
        background:none !important;
    }
    .table-row.hover-styles .item-list-actions::before, .table-row:hover .item-list-actions::before {
        content:none !important;
    }
    svg, body svg *, svg path, .left-sidebar-icon-wrapper *, .scaled-icon {
        background:none !important;
    }
    .is-favorited {
        filter:grayscale(1);
    }
    .modal-backdrop {
        background-color: rgba(0,0,0,0.6) !important;
    }
    .modal, .modal-dialog-container {
        background:none !important;
    }
    .tab {
        border-bottom:5px solid transparent !important;
        padding-left:10px !important;
    }
    .tab.is-selected {
        background:none !important;
        border-bottom:5px solid rgba(20,20,50,0.9) !important;
    }
    .notification.info {
     background:rgba(20,50,20,0.5) !important;
    }
    .notification * {
        background:none !important;
    }
}
}
