style.css:
@-moz-document domain("youtube.com") {
#player-control-overlay.fadein {
    background-color: rgba(0, 0, 0, 0);
}

ytm-custom-control .player-controls-middle {
    left: 20px;
    width: auto;
    top: 75%;
 	
}

.player-controls-middle .icon-button {
    background-color: #00000055;
    border-radius: 15px;
    opacity: 0.35;
}

/* Settings icon */
.player-controls-top {
    opacity: 0.5;
}


}
