style.css:
@-moz-document domain("javascriptweekly.com") {
body {
  background-color: #333333;
  border-top: #292929;
}

BUTTON.awesome {
  color: #d4d4d4;
  border: 1px solid #292929;
  background-image: linear-gradient(to bottom, #292929, #292929);
}

code {
  background-color: #666666 !important;
}

DIV.issue-html {
  box-shadow: inset 0 0 1px #eee;
}

div.confirm h2 {
  color: #d4d4d4;
}

div.issues div.issue {
  color: #d4d4d4;
}

div.main h2.subtitle {
  color: #d4d4d4;
}

div.main p {
  color: #d4d4d4;
}

FORM.subscribe INPUT.email {
  color: #d4d4d4;
  background: #505050;
}

h1 {
  color: #d4d4d4 !important;
  background-color: #292929 !important;
}

INPUT:focus {
  outline: none;
  border: 1px solid #292929;
  border-radius: 2px;
  box-shadow: 0px 0px 2px #292929;
}

nav div.sitename a {
  color: #d4d4d4;
  background-color: #292929;
}

section.contained {
  color: #d4d4d4;
}

table.content.el-content {
  color: #d4d4d4 !important;
  background-color: #444444
}

.desc {
  color: #d4d4d4 !important;
}

.el-heading tbody tr td p {
  border-bottom-color: #292929 !important;
}

.el-masthead {
  background-color: #292929 !important;
  color: #d4d4d4 !important;
}

.mainlink code {
  background-color: rgba(255,255,255,0) !important;
}

.smallprint {
  color: #d4d4d4;
}

.subscribe_box {
  background-color: #333333;
}

.tag-sponsor {
  color: #d4d4d4 !important;
  background-color: #292929 !important;
  border: 1px solid #292929 !important;
}
}
