style.css:
@-moz-document url-prefix("https://www.shadertoy.com/view/") {
#content {
    margin: 0 !important;
    width: 100% !important;
    height: 100vh !important;
}

#header, #compliance, #footer, .block1, .block2, .playerBar, .shaderInfoA{
    display:none !important;
}

.container {
    padding: 0;
}

#player {
    border-radius: 0;
}

.playerCanvas {
    border-radius: 0;
    width: 100vw;
    height: 100vh;
}
}
