style.css:
@-moz-document domain("slack.com") {
div.p-workspace--context-pane-expanded  { grid-template-columns: 128px auto 492px; }
div.p-workspace--context-pane-collapsed { grid-template-columns: 128px auto; }
div.p-classic_nav__team_header          {                 width: 128px; }
nav.p-channel_sidebar                   {                 width: 128px; transition: all 150ms ease; }
nav.p-channel_sidebar:hover             {                 width: 256px; z-index:205;}

}
