style.css:
@-moz-document domain("css-tricks.com") {
body.single-post,
body.single-screenshot {
    background: #100E17;
}

.on-light,
.on-light .article-content {
    background: #100E17;
    color: #fff;
}

.article-content h1,
.article-content h2,
.article-content h3,
.article-content h4,
.article-content h5, 
.article-content h6 {
    color: #FFF;
}

a:not(.article-headline-link) {
    filter: brightness(150%);
}
}
