style.css:
@-moz-document url(https://rooster.hetmml.nl/) {
form#search, body {
    background-color: #303030 !important;
}
html {
    color: white !important;
}
body.no-input #search input[type="search"] {
    background-color: #515151;
    caret-color: white;
}
body.no-input #search input::placeholder {
    color: #959595;
}
body.no-input .tooltip, body.no-input .tooltip::before {
    background-color: #515151;
}
#search input[type="search"] {
    background-color: #515151;
    transition: .125s;
}
#search input[type="search"]:focus {
    background-color: #5e5e5e;
    caret-color: white;
}
input::placeholder, input:focus::placeholder {
    color: white;
}
#search #overflow-button {
    color: #757575;
}
.mdl-menu__outline {
    background: #515151;
}
.mdl-menu__item {
    color: white;
}
#search input[type="search"]:focus {
    color: #FFF;
}
.autocomplete-wrapper {
    background-color: #515151;
}
.autocomplete li:hover {
    background-color: #5E5E5E;
}
#week-selector {
    background-color: #474747;
}
#week-selector button:focus, #search #overflow-button:focus, #search .fav:focus {
    background-color: #4d4d4d;
}
}
