style.css:
@-moz-document regexp('https://www.sousetsuka.com/\\d+?/\\d+?/.*') {
/* Hide Content */
#content-top {
    display: none !important;
}
/* Reading Area */
.main-wrapper {
	width: auto !important;    
}
/* Background */
body, .content-wrapper, .menucodenirvana, ul {
    background-color: #1a1a1a !important;
    box-shadow: none !important;
}
/* Text and Story */
.post-body, p, span {
    color: #8c8c8c !important;
}
/* Titles and Links */
a, h1, h2, h3, h4, h5, h6 {
	color: #ebebeb !important;
}
}
