style.css:
@-moz-document url-prefix("https://www.biblegateway.com/passage/") {
/* Hide Content */
.sidebar, .passage-resources {
    display: none !important;
}
/* Background */
body, select, .form-control, .passage-bible, .passage-bar, .passage-tools2, .passage-text, .passage-col, .prev-chapter, .next-chapter, .passage-scroller {
    background-color: #1a1a1a !important;
}
/* Text and Story */
p, strong, .form-control {
    color: #8c8c8c !important;
}
/* Word of Jesus Red */
.woj {
    color: #ab2b18 !important;
}
/* Titles and Links */
option, h1, h2, h3, h4, h5, h6 {
    color: #ebebeb !important;
}
/* Reading Area */
.passage-main-wrap {
    margin-left: 0px !important;
    margin-right: 0px !important;
}
}
