style.css:
@-moz-document regexp('https?\:\/\/(www\.)?music\.amazon\.[a-z]{2,}.*') {
  #content-wrapper {
      background: #222b3b linear-gradient(180deg,#252d42,#1d1f26);
      border-left: 1px solid hsla(0,0%,100%,.12);
  }

  #content-wrapper * {
      color: #ddd !important;
  }

  .albumList .subHeader, .songList .subHeader {
      background: none;
  }

  .albumBody tr:hover, .songTracks tr:hover {
      background: #3a4255;
  }

  .playlistDetailsListItem:hover:not(.emptyRow):not(.header) td,
  .playlistDetailsListItem:hover {
      transition: none;
      background: #3a4255;
  }
}
