style.css:
@-moz-document domain("theotherturret.wordpress.com") {
/* Background */
body, ul, input, .search-form-icon:before {
    background-color: #1a1a1a !important;
}
/* Text and Story */
p {
    color: #8c8c8c !important;
    margin: 0 0 0.6rem !important;
}
/* Titles and Links */
a, b, h1, h2, h3, h4, h5, h6  {
    color: #ebebeb !important;
}
/* Reading Area */
.site-main {
    margin: 0;
}
}
